import styled from 'styled-components';
import MyList from './StyledList';

const StyledCard = styled.div`
    display : flex;
    flex-direction: column;
    border: 1px solid rgba(0,0,0,.125);
    border-radius: .25rem;
`
const StyledBody = styled.div`
    flex: 1 1 auto;
    min-height: 1px;
    padding:  ${props => props.flush ? 0 : "1.25rem"};
`

const StyledTitle = styled.h5`
    margin-bottom: .75rem;
    font-weight: 500;
    line-height: 1.2;
    font-size: 1.25rem
`
const StyledSubtitle = styled.h6`
        margin-bottom: 0;
        margin-top: -.375rem;
        font-size: 1rem;
        font-weight: 500;
        line-height: 1.2;
`
const StyledText = styled.p`
        margin-top: 0;
`

const StyledHeader = styled.div`
    padding: .75rem 1.25rem;
    margin-bottom: 0;
    background-color: rgba(0,0,0,.03);
    border-bottom: 1px solid rgba(0,0,0,.125);
    display : block;
`


const MyCard = (props) => {
    let title = null;
    let subtitle = null;
    let header = null;
    let items = null;
    let flush = false;
    if (props.title) {
        title = <StyledTitle>{props.title}</StyledTitle>
    }
    if (props.subtitle){
        subtitle = <StyledSubtitle>{props.subtitle}</StyledSubtitle>
    }
    if (props.header){
        header = <StyledHeader>{props.header}</StyledHeader>
    }
    if (props.items) {
        items = <MyList items={props.items} />
        flush = true;
    }
    return (
        <StyledCard>
            {header}
            <StyledBody flush={flush}>
            {title}
            {subtitle}
            {items}
                <StyledText>{props.text}</StyledText>
            </StyledBody>
        </StyledCard>
    )
}

export default MyCard;
