import styled from 'styled-components';

const StyledJumbo = styled.div`
    padding: 2rem 1rem;
    margin-bottom: 2rem;
    background-color: #0086c3;
    border-radius: .3rem;
    text-align : center;
    border : 1px solid black;
`

export default StyledJumbo;
