import styled from 'styled-components';

const StyledMenuButton = styled.button`
    display: block;
    padding: .25rem .75rem;
    font-size: 1.25rem;
    line-height: 1;
    background-color: transparent;
    border: 1px solid transparent;
    border-radius: .25rem;

    @media only screen and (min-width: 900px) {
        display : none;
    }
`

export default StyledMenuButton;
