import React  from 'react';
import Nav from 'react-bootstrap/Nav';
import Link from 'next/link';
import styled from 'styled-components';
import SLink from './StyledLink';

const SiteList = styled.ul`
    display : flex;
    width : 100%;
    justify-content : space-evenly;
    flex-direction : row;
    padding-left: 0;
    margin-bottom: 0;
    list-style: none;
    margin-top : 0;
    border-bottom: 1px solid black;
`

const SiteBar = styled.nav`
    display : flex;
    flex-basis: auto;
    flex-grow: 1;
    align-items: center;
`


function SiteNav(props) {
    let homeActive = false;
    let aboutActive = false;
    let portfolioActive = false;
    let skillsActive = false;
    let resumeActive = false;
    let contactActive = false;

    if (props.page === "home"){
        homeActive = true;
    }
    if (props.page === "about") {
        aboutActive = true;
    }
    if (props.page === "portfolio") {
        portfolioActive = true;
    }
    if (props.page === "skills") {
        skillsActive = true;
    }
    if (props.page === "resume") {
        resumeActive = true;
    }
    if (props.page === "contact") {
        contactActive = true;
    }

    return (
            <SiteBar>
                <SiteList>
                    <SLink url="/" name="Home" />
                    <SLink url="/about" name="About" />
                    <SLink url="/portfolio" name="Portfolio" />
                    <SLink url="/skills" name="Skills" />
                    <SLink url="/resume" name="Resume(PDF)" />
                    <SLink url="/contact" name="Contact" />
                </SiteList>
            </SiteBar>
    )
}

export default SiteNav;
